import { useState, useEffect } from "react";
import React from "react";

function MyPage() {
  const [content_type, setContent_type] = useState("");
  const [title, setTitle] = useState("");
  const [overview, setOverview] = useState("");

  function submitForm(e) {
    e.preventDefault();
    let newIdea = {
      content_type: content_type,
      title: title,
      overview: overview,
    };
    const configObj = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        content_type: content_type,
        title: title,
        overview: overview,
      }),
    };
    fetch("http://localhost:3001/ideas", configObj)
      .then((res) => res.json())
      .then((data) => console.log(data));
  }
  return (
    <div className="MyPage">
      <h1>Post A New Ad</h1>

      <form onSubmit={submitForm}>
        <h3>Will this be a TV Show or Movie?</h3>
        <br />
        <label for="movie">
          This will be a Movie
          <input
            type="radio"
            id="movie"
            name="film_type"
            value="movie"
            onChange={(e) => setContent_type(e.target.id)}
          ></input>
        </label>
        <label for="tv">
          This will be a TV Show
          <input
            type="radio"
            id="tv"
            name="film_type"
            value="tv show"
            onChange={(e) => setContent_type(e.target.id)}
          ></input>
        </label>
        <h3>Add a Title and Overview to your Work</h3>
        <br />
        <label for="title">
          Title
          <input
            type="text"
            id="title"
            placeholder="name your masterpiece here..."
            onChange={(e) => setTitle(e.target.value)}
          ></input>
        </label>
        <label for="overview">
          Overview
          <input
            type="text"
            id="overview"
            placeholder="summary here..."
            onChange={(e) => setOverview(e.target.value)}
          ></input>
        </label>
        <input type="submit"></input>
      </form>
      <h1>My Adds</h1>
    </div>
  );
}

export default MyPage;
